import React, { useState, useEffect } from 'react';
import useTitle from '../hooks/useTitle';
import Loading from '../components/Loading';
import Button from '../components/Button';
import {
  Table, Header, Body, Row, Column
} from '../components/Table';
import { BUTTON_SIZES, ALERT_VARIANTS } from '../config/constants';
import AlertContext from '../contexts/Alert';
import useApi from '../hooks/useApi';

export default () => {
  useTitle('Strona główna');

  const api = useApi();

  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const { createAlert } = AlertContext();

  const getDataFromService = async () => {
    let response;

    try {
      response = await api.get('/api/users/');
    } catch (error) {
      createAlert(
        `Błąd pobierania użytkowników z serwisu ${String.fromCodePoint(0x26a0)}`,
        ALERT_VARIANTS.warning
      );
      return;
    }

    if (response.status === 200) {
      setItems(response.data.users);
    }
    setIsLoading(false);
  };

  async function changeUserDetails(accountActive) {
    let response;
    try {
      response = await api.get('/api/activate_user/');
    } catch (error) {
      createAlert(
        `Błąd zmiany danych użytkownika ${String.fromCodePoint(0x26a0)}`,
        ALERT_VARIANTS.warning
      );
      return;
    }

    if (response.status === 200) {
      createAlert(
        'Zmiana danych użytkownika zakończona pomyślnie!',
        ALERT_VARIANTS.success
      );
    }
    getDataFromService();
  }

  useEffect(() => {
    getDataFromService();
  }, []);

  return (
    <Loading isActive={isLoading}>
      <div />
      <h1>Panel aktywacji użytkowników</h1>
      <Table>
        <Header>
          <Row>
            <Column as="th">Użytkownik</Column>
            <Column as="th">Konto aktywne</Column>
            <Column as="th">Akcja</Column>
          </Row>
        </Header>

        <Body>
          {items.map(item => (
            <Row key={item.id}>
              <Column>{item.username}</Column>
              <Column>
                <p>{item.is_active ? 'Aktywne' : 'Nieaktywne'}</p>
              </Column>
              <Column>
                <Button as="a" size={BUTTON_SIZES.small} onClick={() => changeUserDetails(item.is_active)}>
                  {item.is_active ? 'Deaktywuj' : 'Aktywuj'}
                </Button>
              </Column>
            </Row>
          ))}
        </Body>
      </Table>
    </Loading>
  );
};
