import React, { useState, useEffect } from 'react';
import AlertContext from '../contexts/Alert';
import useTitle from '../hooks/useTitle';
import Loading from '../components/Loading';
import Button from '../components/Button';
import Separator from '../components/Separator';
import UploadModal from './partials/UploadModal';
import DeleteModal from './partials/DeleteModal';
import {
  Table, Header, Body, Row, Column
} from '../components/Table';
import useApi from '../hooks/useApi';
import { ALERT_VARIANTS, API_URL } from '../config/constants';

export default () => {
  useTitle('Manager plik√≥w');

  const api = useApi();
  const { createAlert } = AlertContext();

  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isUploadModal, setIsUploadModal] = useState(false);
  const [isDeleteModal, setIsDeleteModal] = useState(false);

  const fetchList = async () => {
    setIsLoading(true);

    let response;

    try {
      response = await api.get('/api/images/');
    } catch (ex) {
      createAlert(
        `B≈ÇƒÖd podczas ≈Çadowania plik√≥w ${String.fromCodePoint(0x26a0)}`,
        ALERT_VARIANTS.warning
      );

      setIsLoading(false);
      return;
    }

    setItems(response.data.images);
    setIsLoading(false);
  };

  const openUploadModal = () => setIsUploadModal(true);
  const closeUploadModal = () => setIsUploadModal(false);

  const openDeleteModal = id => setIsDeleteModal(id);
  const closeDeleteModal = () => setIsDeleteModal(false);

  useEffect(() => {
    fetchList();
  }, []);

  return (
    <Loading isActive={isLoading}>
      <Button onClick={openUploadModal}>Dodaj plik</Button>

      <Separator />

      <UploadModal
        isOpen={isUploadModal}
        onClose={closeUploadModal}
        onDone={() => {
          fetchList();
          closeUploadModal();
        }}
      />

      <DeleteModal
        id={isDeleteModal}
        isOpen={isDeleteModal}
        onClose={closeDeleteModal}
        onDone={() => {
          fetchList();
          closeDeleteModal();
        }}
      />

      <Table>
        <Header>
          <Row>
            <Column as="th">Nazwa</Column>
            <Column as="th" />
          </Row>
        </Header>

        <Body>
          {items.map(item => (
            <Row key={item.id}>
              <Column>
                <a target="_blank" rel="noopener noreferrer" href={API_URL + item.pic}>
                  {item.pic_text}
                </a>
              </Column>

              <Column>
                <Button onClick={() => openDeleteModal(item.id)}>
                  Usu≈Ñ
                </Button>
              </Column>
            </Row>
          ))}
        </Body>
      </Table>
    </Loading>
  );
};
