import React from 'react';
import { Route as RouteView, Redirect } from 'react-router-dom';
import UserContext from '../../contexts/User';
import AlertContext from '../../contexts/Alert';
import { ALERT_VARIANTS } from '../../config/constants';

export default ({
  component: Component, unauthorized, authorized, ...rest
}) => {
  const { createAlert } = AlertContext();
  const { user } = UserContext();

  return (
    <RouteView
      {...rest}
      render={(props) => {
        // const { state } = props.history.location;
        // if (state && !state.auth) {
        // }

        if (unauthorized && user) {
          createAlert(
            `Strona dostępna tylko dla gości ${String.fromCodePoint(0x1f62e)}`,
            ALERT_VARIANTS.warning
          );

          return <Redirect to="/" />;
        }

        if (authorized && !user) {
          createAlert(`Wymagane logowanie ${String.fromCodePoint(0x26a0)}`, ALERT_VARIANTS.warning);

          return <Redirect to="/zaloguj" />;
        }

        return <Component {...props} />;
      }}
    />
  );
};
